const LANGUAGE = "hebrew";
const data = LANGUAGE === 'hebrew' ? raw_data.hebrew : raw_data.english;

const getLogo = (link) => {
    const directory = "/assets/images/";

    if (link.includes("ynet"))
        return directory + "ynet.png"

    if (link.includes("themarker"))
        return directory + "themarker.png"

    if (link.includes("mako"))
        return "https://play-lh.googleusercontent.com/RV67cORBjAWcGLrqCUg3npkKrfjs24tIDSvQWHgmFQ9PDiNmkyxk5O4Rw6bwYJOMEopL=w480-h960-rw"

    if (link.includes("walla"))
        return directory + "walla.png"

    if (link.includes("globes"))
        return directory + "globes.png"

    if (link.includes("calcalist"))
        return directory + "calcalist.png"

    if (link.includes("maariv"))
        return "https://play-lh.googleusercontent.com/oeTE8Q1DH2Vc6jXtrT3ZbPkiES0Zzcd8GXCcIwbPNvplgbLCn1pgWC_geusfqG3UvJYa"

    return "https://cdn-icons-png.flaticon.com/512/3593/3593455.png"
}

document.addEventListener('DOMContentLoaded', () => {
    const sections = document.getElementById('page-container');

    data.sections.forEach((section) => {
        const sectionElement = document.createElement('div');
        sectionElement.classList.add('section');
        sectionElement.id = `section-${section.href}`;

        const special = section.clients || section.articles;

        sectionElement.innerHTML = `
            <div class="Title">
                <span>#</span>
                <h4>${section.title}</h4>
            </div>`;

        if (special) sectionElement.innerHTML += `<span class="has-block">${section.text}</span>`;
        else sectionElement.innerHTML += `<span>${section.text}</span>`;


        if (section.crises) {
            const crisesContainer = document.createElement('div');
            crisesContainer.classList.add('crises-container');

            section.crises.forEach((crisis) => {
                const crisisElement = document.createElement('div');

                crisisElement.innerHTML = `
                    <img src="assets/check.svg" alt=""/>
                    <div>
                        <b>${crisis.crisis}</b>
                        <span>לקוח: ${crisis.client}</span>
                    </div>
                `;
                crisesContainer.appendChild(crisisElement);
            });
            sectionElement.appendChild(crisesContainer);
        }

        if (section.clients) {
            const clientsContainer = document.createElement('div');
            clientsContainer.classList.add('clients-container');

            section.clients.forEach((client) => {
                const clientElement = document.createElement('div');
                clientElement.innerHTML = `
                    <div>
                        <img src="${client.logo}" alt=""/>
                    </div>
                    <span>${client.name}</span>
                `;
                if (client.link) {
                    clientElement.classList.add('hoberable');
                    clientElement.addEventListener('click', () => window.open(client.link, "_blank"));
                }
                clientsContainer.appendChild(clientElement);
            });

            sectionElement.appendChild(clientsContainer);
        }

        if (section.articles) {
            const articlesContainer = document.createElement('div');
            articlesContainer.classList.add('articles-container');

            section.articles.forEach((article) => {
                const articleElement = document.createElement('div');
                articleElement.innerHTML = `
                    <div>
                        <img src=${getLogo(article.link)} alt="לוגו"/>
                        <b>${article.title}</b>
                    </div>
                    <span>${article.content}</span>
                `; //
                articleElement.classList.add('hoberable');
                articleElement.addEventListener('click', () => window.open(article.link, "_blank"));
                articlesContainer.appendChild(articleElement);
            });

            sectionElement.appendChild(articlesContainer);
        }

        // create div with class Content and add it to the section
        const content = document.createElement('div');
        content.classList.add('Content');

        content.appendChild(sectionElement);
        sections.appendChild(content);
    });


    const contact = document.createElement('div');
    contact.classList.add('section');
    contact.id = 'section-contact';
    contact.innerHTML = `
        <div class="Title">
            <h4>${data.contact.title}</h4>
        </div>

        <div class="contactable">
            <div>
                <img src="assets/phone.svg" alt=""/>
                <span>${data.contact.phone}</span>
            </div>

            <div>
                <img src="assets/email.svg" alt=""/>
                <span>shalevpr@gmail.com</span>
            </div>

            <div>
                <img src="assets/email.svg" alt=""/>
                <span>shalevp@bezeqint.net</span>   
            </div>   
        </div>`;

    const content = document.createElement('div');
    content.classList.add('Content');
    content.appendChild(contact);
    sections.appendChild(content);

});

const setBackgroundColor = (color) => {
    const hex = color === 'light' ? '#edf0f5' : color === 'dark' ? '#1e1e1e' : color;

    const s = (x) => parseInt(x, 16)

    const [r, g, b] = [s(hex.substring(1, 3)), s(hex.substring(3, 5)), s(hex.substring(5, 7))];

    const factor = 3;

    document.documentElement.style.setProperty("--background-color", `rgb(${r}, ${g}, ${b})`);
    document.documentElement.style.setProperty("--background-color-lighter", `rgb(${r + factor}, ${g + factor}, ${b + factor})`);
    document.documentElement.style.setProperty("--background-color-darker", `rgb(${r - factor}, ${g - factor}, ${b - factor})`);
    document.documentElement.style.setProperty("--background-color-lightest", `rgb(${r + factor * 2.5}, ${g + factor * 2.5}, ${b + factor * 2.5})`);
    document.documentElement.style.setProperty("--background-color-darkest", `rgb(${r - factor * 2.5}, ${g - factor * 2.5}, ${b - factor * 2.5})`);

    document.documentElement.style.setProperty("--dynamic-color", `rgb(${255 - r}, ${255 - g}, ${255 - b})`);
    document.documentElement.style.setProperty("--dynamic-color-transparent", `rgba(${255 - r}, ${255 - g}, ${255 - b}, 0.5)`);
    document.documentElement.style.setProperty("--dynamic-color-lighter", `rgb(${255 - r + factor}, ${255 - g + factor}, ${255 - b + factor})`);
    document.documentElement.style.setProperty("--dynamic-color-darker", `rgb(${255 - r - factor}, ${255 - g - factor}, ${255 - b - factor})`);
    document.documentElement.style.setProperty("--dynamic-color-lightest", `rgb(${255 - r + factor * 2.5}, ${255 - g + factor * 2.5}, ${255 - b + factor * 2.5})`);
    document.documentElement.style.setProperty("--dynamic-color-darkest", `rgb(${255 - r - factor * 2}, ${255 - g - factor * 2}, ${255 - b - factor * 2})`);

    document.documentElement.style.setProperty("--logo-background", color === 'dark' ? `rgb(${255 - r}, ${255 - g}, ${255 - b})` : 'none');
    document.documentElement.style.setProperty("--direction", LANGUAGE === 'hebrew' ? 'rtl' : 'ltr');

}

const scrollToSection = (section) => {
    const element = document.getElementById(`section-${section}`);
    element.scrollIntoView({behavior: "smooth", block: "start", inline: "nearest"});
}


setBackgroundColor('light');
